function App() {
	const dispatch = useDispatch();
	const blockchain = useSelector((state) => state.blockchain);
	const data = useSelector((state) => state.data);
	const [claimingNft, setClaimingNft] = useState(false);
	const [feedback, setFeedback] = useState(`Click buy to mint your NFT.`);
	const [mintAmount, setMintAmount] = useState(1);
	const [CONFIG, SET_CONFIG] = useState({
		CONTRACT_ADDRESS: '',
		SCAN_LINK: '',
		NETWORK: {
			NAME: '',
			SYMBOL: '',
			ID: 0,
		},
		NFT_NAME: '',
		SYMBOL: '',
		MAX_SUPPLY: 1,
		WEI_COST: 0,
		DISPLAY_COST: 0,
		GAS_LIMIT: 0,
		MARKETPLACE: '',
		MARKETPLACE_LINK: '',
		SHOW_BACKGROUND: false,
	});

	const claimNFTs = () => {
		let cost = CONFIG.WEI_COST;
		let gasLimit = CONFIG.GAS_LIMIT;
		let totalCostWei = String(cost * mintAmount);
		let totalGasLimit = String(gasLimit * mintAmount);
		console.log('Cost: ', totalCostWei);
		console.log('Gas limit: ', totalGasLimit);
		setFeedback(`Minting your ${CONFIG.NFT_NAME}...`);
		setClaimingNft(true);
		blockchain.smartContract.methods
			.mint(mintAmount)
			.send({
				gasLimit: String(totalGasLimit),
				to: CONFIG.CONTRACT_ADDRESS,
				from: blockchain.account,
				value: totalCostWei,
			})
			.once('error', (err) => {
				console.log(err);
				setFeedback('Sorry, something went wrong please try again later.');
				setClaimingNft(false);
			})
			.then((receipt) => {
				console.log(receipt);
				setFeedback(
					`WOW, the ${CONFIG.NFT_NAME} is yours! go visit Opensea.io to view it.`
				);
				setClaimingNft(false);
				dispatch(fetchData(blockchain.account));
			});
	};

	const decrementMintAmount = () => {
		let newMintAmount = mintAmount - 1;
		if (newMintAmount < 1) {
			newMintAmount = 1;
		}
		setMintAmount(newMintAmount);
	};

	const incrementMintAmount = () => {
		let newMintAmount = mintAmount + 1;
		if (newMintAmount > 10) {
			newMintAmount = 10;
		}
		setMintAmount(newMintAmount);
	};

	const getData = () => {
		if (blockchain.account !== '' && blockchain.smartContract !== null) {
			dispatch(fetchData(blockchain.account));
		}
	};

	const getConfig = async () => {
		const configResponse = await fetch('/config/config.json', {
			headers: {
				'Content-Type': 'application/json',
				Accept: 'application/json',
			},
		});
		const config = await configResponse.json();
		SET_CONFIG(config);
	};

	useEffect(() => {
		getConfig();
	}, []);

	useEffect(() => {
		getData();
	}, [blockchain.account]);

	return (
		<div>
			<div>
				{data.totalSupply} / {CONFIG.MAX_SUPPLY}
				<StyledLink target={'_blank'} href={CONFIG.SCAN_LINK}>
					{truncate(CONFIG.CONTRACT_ADDRESS, 15)}
					{Number(data.totalSupply) >= CONFIG.MAX_SUPPLY ? (
						<>
							<s.TextTitle
								style={{ textAlign: 'center', color: 'var(--accent-text)' }}
							>
								The sale has ended.
							</s.TextTitle>
							<s.TextDescription
								style={{ textAlign: 'center', color: 'var(--accent-text)' }}
							>
								You can still find {CONFIG.NFT_NAME} on
							</s.TextDescription>
							<s.SpacerSmall />
							<StyledLink target={'_blank'} href={CONFIG.MARKETPLACE_LINK}>
								{CONFIG.MARKETPLACE}
							</StyledLink>
						</>
					) : (
						<>
							<s.TextTitle
								style={{ textAlign: 'center', color: 'var(--accent-text)' }}
							>
								1 {CONFIG.SYMBOL} costs {CONFIG.DISPLAY_COST}{' '}
								{CONFIG.NETWORK.SYMBOL}.
							</s.TextTitle>
							<s.SpacerXSmall />
							<s.TextDescription
								style={{ textAlign: 'center', color: 'var(--accent-text)' }}
							>
								Excluding gas fees.
							</s.TextDescription>
							<s.SpacerSmall />
							{blockchain.account === '' ||
							blockchain.smartContract === null ? (
								<s.Container ai={'center'} jc={'center'}>
									<s.TextDescription
										style={{
											textAlign: 'center',
											color: 'var(--accent-text)',
										}}
									>
										Connect to the {CONFIG.NETWORK.NAME} network
									</s.TextDescription>
									<s.SpacerSmall />
									<StyledButton
										onClick={(e) => {
											e.preventDefault();
											dispatch(connect());
											getData();
										}}
									>
										CONNECT
									</StyledButton>
									{blockchain.errorMsg !== '' ? (
										<>
											<s.SpacerSmall />
											<s.TextDescription
												style={{
													textAlign: 'center',
													color: 'var(--accent-text)',
												}}
											>
												{blockchain.errorMsg}
											</s.TextDescription>
										</>
									) : null}
								</s.Container>
							) : (
								<>
									<s.TextDescription
										style={{
											textAlign: 'center',
											color: 'var(--accent-text)',
										}}
									>
										{feedback}
									</s.TextDescription>
									<s.SpacerMedium />
									<s.Container ai={'center'} jc={'center'} fd={'row'}>
										<StyledRoundButton
											style={{ lineHeight: 0.4 }}
											disabled={claimingNft ? 1 : 0}
											onClick={(e) => {
												e.preventDefault();
												decrementMintAmount();
											}}
										>
											-
										</StyledRoundButton>
										<s.SpacerMedium />
										<s.TextDescription
											style={{
												textAlign: 'center',
												color: 'var(--accent-text)',
											}}
										>
											{mintAmount}
										</s.TextDescription>
										<s.SpacerMedium />
										<StyledRoundButton
											disabled={claimingNft ? 1 : 0}
											onClick={(e) => {
												e.preventDefault();
												incrementMintAmount();
											}}
										>
											+
										</StyledRoundButton>
									</s.Container>
									<s.SpacerSmall />
									<s.Container ai={'center'} jc={'center'} fd={'row'}>
										<StyledButton
											disabled={claimingNft ? 1 : 0}
											onClick={(e) => {
												e.preventDefault();
												claimNFTs();
												getData();
											}}
										>
											{claimingNft ? 'BUSY' : 'BUY'}
										</StyledButton>
									</s.Container>
								</>
							)}
						</>
					)}
				</StyledLink>
				Please make sure you are connected to the right network (
				{CONFIG.NETWORK.NAME} Mainnet) and the correct address. Please note:
				Once you make the purchase, you cannot undo this action. We have set the
				gas limit to {CONFIG.GAS_LIMIT} for the contract to successfully mint
				your NFT. We recommend that you don't lower the gas limit.
			</div>
			<NavBar menuLinks={menuLinks} logoText={logoText} />
			<Banner banner={banner} bannerImages={bannerImages} />
			<BuyAnApe buyAnApeText={buyAnApeText} />
			{/* <RoadMap
				roadMapGoalsArray={roadMapGoalsArray}
				roadMapSubtitleText={roadMapSubtitleText}
			/> */}
			{/* <JoinCommunity joinTheCommunity={joinTheCommunity} /> */}
